        "data": {
            "templateName": "selfDeclaration",
            "action": "submit",
            "fields": [
                {
                    "code": "externalIds",
                    "type": "nested_group",
                    "children": [
                        {
                            "code": "declared-ext-id",
                            "fieldName": "Your ID from State/Board/Org",
                            "type": "input",
                            "templateOptions": {
                                "labelHtml": {
                                    "contents": "<span>$0</span>",
                                    "values": {
                                        "$0": "ID as requested by your State/ Board/ Org"
                                    }
                                },
                                "placeHolder": "Enter ID"
                            },
                            "validations": [
                                {
                                    "type": "pattern",
                                    "value": "^[^\"',\\r\\n|\\r|\\n]*$",
                                    "message": "This field does not allow you to enter special characters"
                                }
                            ]
                        },
                        {
                            "code": "declared-phone",
                            "fieldName": "Mobile Number",
                            "type": "input",
                            "templateOptions": {
                                "labelHtml": {
                                    "contents": "<span>$0</span>",
                                    "values": {
                                        "$0": "Mobile Number for State/ Org"
                                    }
                                },
                                "placeHolder": "Enter Mobile Number",
                                "prefix": "+91 -"
                            },
                            "validations": [
                                {
                                    "type": "pattern",
                                    "value": "^[6-9*][0-9*]{9}$",
                                    "message": "Enter a valid mobile number"
